import "./Receta.css";
import {Card} from 'react-bootstrap';
import { Link } from "react-router-dom";
import {useState, useEffect  } from "react";
import MydModalWithGrid from '../ModalRecipe/ModalRecipe.js'
import configData from "../../config.json";


function Receta(){    
    const [showModal, setShow] = useState(false);
    const [Recetas, setReceta] = useState([]);
      /* useEffect(()=>{
          fetch(`./json/id/${props.id}.json`)
          .then((response)=>response.json())
          .then((data)=>setReceta(data));
      },[]);
      */
      /* if (props.ingredients) {
         console.log(Receta);+
      } */
      useEffect(()=>{
        fetch(`${configData.SERVER_URL}/recetas`, {
          method: 'GET',
          headers: new Headers({ 'Content-type': 'application/json'}),
          mode: 'no-cors'
  })
        .then((response)=>response.json())
        .then((data)=>setReceta(data));
    },[]);
    if (!Recetas) return null;

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    {Recetas.map((Receta) => {
    return(
        <Card style={{width:'12rem'}} className="Card sm-2">
        <Link to="#" onClick={handleShow} className="Cardlink" >
        <Card.Img className="CardImage"variant="top" src={Receta.image} />
        <Card.Body className="CardBody">
            <Card.Subtitle className="CardSubtitle mb-2 text-muted">{Receta.name}</Card.Subtitle> 
        </Card.Body>
        </Link>
        <MydModalWithGrid id={Receta._id.$oid} show={showModal} onHide={handleClose} />
    </Card>
    );
    })}
}

export default Receta;
